import React, { useEffect } from "react";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";
import * as userSelectors from "../../selectors/user";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { getUserProfile } from "../../thunks/user";

const Profile = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);
  const profile = userSelectors.getProfile(user);
  const auth = userSelectors.getUser(user);

  useEffect(() => {
    dispatch(getUserProfile());
  }, []);

  const userType = userSelectors.getUserType(auth);

  const name = userSelectors.getName(profile);
  const birthday = userSelectors.getBirthday(profile);
  const gender = userSelectors.getGender(profile);
  const race = userSelectors.getRace(profile);
  const religion = userSelectors.getReligion(profile);
  const address = userSelectors.getAddress(profile);
  const telNo = userSelectors.getTelNo(profile);
  const hpNo = userSelectors.getHpNo(profile);
  const email = userSelectors.getEmail(profile);
  const schoolId = userSelectors.getSchoolId(profile);
  const disability = userSelectors.getDisability(profile);

  return (
    <div className="mx-auto my-4 center">
      <h2 className="my-2">Profile Details</h2>
      <Card className="py-5 px-3">
        <Container>
          <Row className="d-flex align-items-lg-center">
            <Col xs={12} lg={5} className="imageContainer">
              <div className="circle"></div>
            </Col>

            <Col xs={12} lg={7}>
              <Row>
                <Col xs={6} lg={5}>
                  Name
                </Col>
                <Col xs={6} lg={7}>
                  {name}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={5}>
                  Date of Birth
                </Col>
                <Col xs={6} lg={7}>
                  {birthday}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={5}>
                  Gender
                </Col>
                <Col xs={6} lg={7}>
                  {gender}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={5}>
                  Race
                </Col>
                <Col xs={6} lg={7}>
                  {race}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={5}>
                  Religion
                </Col>
                <Col xs={6} lg={7}>
                  {religion}
                </Col>
              </Row>
              {userType === 1 && (
                <>
                  <Row>
                    <Col xs={6} lg={5}>
                      School ID
                    </Col>
                    <Col xs={6} lg={7}>
                      {schoolId}
                    </Col>
                  </Row>
                  <Row>
                    <Col xs={6} lg={5}>
                      Class
                    </Col>
                    <Col xs={6} lg={7}>
                      -
                    </Col>
                  </Row>
                </>
              )}
            </Col>
          </Row>
          <Row>
            <br />
            <br />
          </Row>
          <Row>
            <Col>
              <Row>
                <Col xs={6} lg={4}>
                  Address
                </Col>
                <Col xs={6} lg={4}>
                  {address}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={4}>
                  Tel No
                </Col>
                <Col xs={6} lg={4}>
                  {telNo}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={4}>
                  Hp No
                </Col>
                <Col xs={6} lg={4}>
                  {hpNo}
                </Col>
              </Row>
              <Row>
                <Col xs={6} lg={4}>
                  Email
                </Col>
                <Col xs={6} lg={4}>
                  {email}
                </Col>
              </Row>

              <Row>
                <Col xs={6} lg={4}>
                  Disability
                </Col>
                <Col xs={6} lg={4}>
                  {disability}
                </Col>
              </Row>
            </Col>
          </Row>
        </Container>
      </Card>
    </div>
  );
};

export default Profile;
