import React, { useCallback, useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { USER_TYPE_NUMBER } from "../../utilities/constants";

const RegisterUser = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState(null);
  const isDisabled = () => {
    return username === "" || password === "" || userType === null;
  };
  return (
    <div className="mx-auto my-4 w-75">
      <h3>Register User</h3>
      <Card className="py-5 px-5 justify-content-flex-start">
        <Form>
          <Form.Group>
            <Form.Label>Username</Form.Label>
            <Form.Control type="text" value="" />
          </Form.Group>
          <Form.Group>
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" value="" />
          </Form.Group>
          <Form.Group>
            <Form.Label>User Type</Form.Label>
            <Form.Select value={null}>
              <option value={null}>-- Please Select--</option>
              <option value={USER_TYPE_NUMBER.STUDENT}>Student</option>
              <option value={USER_TYPE_NUMBER.TEACHER}>Teacher</option>
            </Form.Select>
          </Form.Group>
          <Button disabled={isDisabled()}>Submit</Button>
        </Form>
      </Card>
    </div>
  );
};

export default RegisterUser;
