import React, { Fragment, useCallback, useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { USER_TYPE_NUMBER } from "../../utilities/constants";

const RegisterUser = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [userType, setUserType] = useState(0);
  const isDisabled = () => {
    return username === "" || password === "" || userType === 0;
  };
  return (
    <div className="mx-auto my-4 w-75">
      <h3>Register User</h3>
      <Card className="py-5 px-5 justify-content-flex-start">
        <Form>
          <Form.Group>
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>User Type</Form.Label>
            <Form.Select
              value={userType}
              onChange={(e) => setUserType(e.target.value)}
            >
              <option value={0}>-- Please Select--</option>
              <option value={USER_TYPE_NUMBER.STUDENT}>Student</option>
              <option value={USER_TYPE_NUMBER.TEACHER}>Teacher</option>
            </Form.Select>
          </Form.Group>
          {userType !== 0 && (
            <Fragment>
              <Form.Group>
                <Form.Label>Username</Form.Label>
                <Form.Control
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </Form.Group>
            </Fragment>
          )}
          <br />
          <Button disabled={isDisabled()}>Submit</Button>
        </Form>
      </Card>
    </div>
  );
};

export default RegisterUser;
