import React, { useEffect } from "react";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Table from "react-bootstrap/Table";
import { useDispatch, useSelector } from "react-redux";
import { getUserList } from "../../thunks/user";
import * as userSelectors from "../../selectors/user";
import { USER_TYPE } from "../../utilities/constants";

const ManageUser = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);
  useEffect(() => {
    dispatch(getUserList());
  }, [dispatch]);

  const userList = userSelectors.getUserList(user);
  return (
    <div className="mx-auto my-4 w-75">
      <h3 className="my-2">Manage User</h3>
      <Card className="py-5 px-5 justify-content-flex-start">
        <Row>
          <Col lg="auto">
            <Form.Control type="text" placeholder="Search.." />
          </Col>
          <Col />
          <Col lg="auto">
            <Form.Group as={Row} className="d-flex align-items-center">
              <Col>Filter:</Col>
              <Col lg="auto">
                <Form.Select>
                  <option value="all">All User</option>
                  <option value="teacher">Teacher</option>
                  <option value="student">Student</option>
                </Form.Select>
              </Col>
            </Form.Group>
          </Col>
        </Row>
        <Table className="my-4" bordered>
          <thead>
            <tr>
              <th>No.</th>
              <th>Name</th>
              <th>User Type</th>
              <th>Created Date</th>
              <th>Option</th>
            </tr>
          </thead>
          <tbody>
            {userList.map((element, index) => {
              return (
                <tr>
                  <td>{index + 1}</td>
                  <td>{userSelectors.getName(element)}</td>
                  <td>{USER_TYPE[userSelectors.getUserType(element)]}</td>
                  <td>{index + 1}</td>
                  <td>{index + 1}</td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </Card>
    </div>
  );
};

export default ManageUser;
